(16,	'wc-admin-optimizing-the-checkout-flow',	'info',	'en_US',	'Optimizing the checkout flow',	'It\'s crucial to get your store\'s checkout as smooth as possible to avoid losing sales. Let\'s take a look at how you can optimize the checkout experience for your shoppers.',	'{}',	'unactioned',	'woocommerce.com',	'2021-10-10 11:08:58',	NULL,	0,	'plain',	'',	0,	'info',	0),
(17,	'wc-admin-first-five-things-to-customize',	'info',	'en_US',	'The first 5 things to customize in your store',	'Deciding what to start with first is tricky. To help you properly prioritize, we\'ve put together this short list of the first few things you should customize in WooCommerce.',	'{}',	'unactioned',	'woocommerce.com',	'2021-10-09 09:03:39',	NULL,	0,	'plain',	'',	0,	'info',	0),
(18,	'wc-payments-qualitative-feedback',	'info',	'en_US',	'WooCommerce Payments setup - let us know what you think',	'Congrats on enabling WooCommerce Payments for your store. Please share your feedback in this 2 minute survey to help us improve the setup process.',	'{}',	'pending',	'woocommerce.com',	'2021-10-07 06:58:52',	NULL,	0,	'plain',	'',	0,	'info',	0),
